//  ------------------------------------------------------------------
//  LETTERS

function Key_Letters(props) {
    return (
        <Frame
            width={32}
            height={42}
            radius={5}
            background="#FCFCFE"
            shadow="0px 1px 0px 0px #898A8D"
            overflow="hidden"
        >
            <Frame
                width="100%"
                height={28}
                top={5}
                background="none"
                style={{
                    fontFamily: SFProText,
                    fontSize: "24px",
                    lineHeight: "4",
                    textAlign: "center",
                }}
                onTap={props.onTap}
            >
                {props.content}
            </Frame>
        </Frame>
    )
}

const letters_rowOne = ["q", "w", "e", "r", "t", "y", "u", "i", "o", "p"]
const letters_rowTwo = ["a", "s", "d", "f", "g", "h", "j", "k", "l"]
const letters_rowThree = ["z", "x", "c", "v", "b", "n", "m"]

//  ------------------------------------------------------------------
//  KEYBOARD COMPONENT

export function Keyboard(props) {
    return (
        <Frame size="100%" background="#D1D5DB">
            {/* Row 1 */}
            <Stack
                top={8}
                left={3}
                right={3}
                height={42}
                direction="horizontal"
                distribution="space-between"
            >
                {letters_rowOne.map((letter, index) => {
                    return (
                        <Key_Letters
                            content={letter}
                            key={index.toString()}
                            onTap={() => {
                                props.onKeyTap(letter)
                            }}
                        />
                    )
                })}
            </Stack>

            {/* Row 2 */}
            <Stack
                top={62}
                left={22}
                right={22}
                height={42}
                direction="horizontal"
                distribution="space-between"
            >
                {letters_rowTwo.map((letter, index) => {
                    return (
                        <Key_Letters
                            content={letter}
                            key={index.toString()}
                            onTap={() => {
                                props.onKeyTap(letter)
                            }}
                        />
                    )
                })}
            </Stack>

            {/* Row 3 */}
            <Stack
                top={116}
                left={3}
                right={3}
                height={42}
                direction="horizontal"
                distribution="space-between"
            >
                <Key_Shift />
                <Stack
                    width={257}
                    height={42}
                    direction="horizontal"
                    distribution="space-between"
                >
                    {letters_rowThree.map((letter, index) => {
                        return (
                            <Key_Letters
                                content={letter}
                                key={index.toString()}
                                onTap={() => {
                                    props.onKeyTap(letter)
                                }}
                            />
                        )
                    })}
                </Stack>
                <Key_Delete
                    onTap={() => {
                        props.onKeyTap("delete")
                    }}
                />
            </Stack>

            {/* Row 4 */}
            <Stack
                top={170}
                left={3}
                right={3}
                height={42}
                direction="horizontal"
                distribution="space-between"
            >
                <Key_123 />
                <Key_Space />
                <Key_At
                    onTap={() => {
                        props.onKeyTap("@")
                    }}
                />
                <Key_Dot
                    onTap={() => {
                        props.onKeyTap(".")
                    }}
                />
                <Key_Return content="go" />
            </Stack>

            {/* Row 5 */}
            <Stack
                top={224}
                left={3}
                right={3}
                height={51}
                direction="horizontal"
                distribution="space-between"
            >
                <Key_Emoji />
            </Stack>
        </Frame>
    )
}

