/* ------------------------------------------------------------------------------------------------------------- */

Status_Bar.defaultProps = {
    width: 375,
    height: 44,
    type: "iOSX",
    mode: "light",
}

addPropertyControls(Status_Bar, {
    type: {
        title: "Type",
        type: ControlType.Enum,
        options: ["iOSX", "iOS8", "pixel3"],
        optionTitles: ["iOS X (44)", "iOS 8 (20)", "Pixel 3 (25)"],
    },
    mode: {
        title: "Mode",
        type: ControlType.SegmentedEnum,
        options: ["light", "dark"],
        optionTitles: ["Light", "Dark"],
    },
})
